void wcc_dtzpad_(char *UPLO, char *HERM, int *M, int *N, int *IOFFD,
                 char *ALPHA, char *BETA, char *A, int *LDA)
{
#ifdef FC_LEN_T
   dtzpad_(UPLO, HERM, M, N, IOFFD, (double*) ALPHA, (double*) BETA,
           (double*) A, LDA,
           (FC_LEN_T) strlen(UPLO), (FC_LEN_T) strlen(HERM));
#else
   dtzpad_(UPLO, HERM, M, N, IOFFD, (double*) ALPHA, (double*) BETA,
           (double*) A, LDA);
#endif
}
